<script>
import anime from 'animejs'

export default {
  name: 'MainSlogan',
  mounted() {
    this.animateSVG()
  },
  methods: {
    animateSVG: () => {
      document.querySelectorAll('.main-slogan__line').forEach((line) => {
        const length = line.getTotalLength()

        line.style.strokeDasharray = length
        line.style.strokeDashoffset = length

        anime({
          targets: line,
          strokeDashoffset: [length, 0],
          duration: 3000,
          easing: 'linear',
          loop: false,
        })
      })

      anime({
        targets: '#pattern_image',
        x: [-3000, 0],
        duration: 5000,
        delay: 1000,
        easing: 'linear',
        loop: false,
      })
    },
  },
}
</script>

